async function getBlockInfo(blockNum:number|string) {
    let blockInfo = web3.eth.getBlock(blockNum);
    return blockInfo
}

const getPastEvents=async function(contractIns:any, _from: number|string, _to:number|string){
    let logs
    try {
          logs = await contractIns.getPastEvents("Transfer",{
            fromBlock: _from,
            toBlock: _to
        });
        // console.log("log is", logs)
    } catch (e) {
        console.log(e);
        return [];
      }
    return logs;
}


